mapper.select:
SELECT t1.new_yn , t3.cd_val AS new_yn_nm , t1.use_yn , t2.cd_val AS use_yn_nm , t1.hospital_nm , t1.hospital_id , t1.hospital_pwd , t1.gen_by , t1.gen_dt , t1.mdfy_by , t1.mdfy_dt , t1.user_type FROM t_hospital_info t1 INNER JOIN t_cm_cd t2 ON t1.use_yn = t2.cd AND t2.cat_cd = 'HOSPITAL_USE_YN' INNER JOIN t_cm_cd t3 ON t1.new_yn = t3.cd AND t3.cat_cd = 'HOSPITAL_NEW_YN' WHERE 1=1 AND t1.user_type = 'USER'
AND (t1.hospital_nm ILIKE CONCAT('%', #{hospital_nm}, '%') OR t1.hospital_id ILIKE CONCAT('%', #{hospital_id}, '%'))
ORDER BY t1.${order_type} ${order_type2}
ORDER BY t1.GEN_DT DESC
LIMIT ${raw_cnt} OFFSET (${page_cnt} - 1) * ${raw_cnt}

mapper.update:
UPDATE t_hospital_info
   SET mdfy_dt = now()

	 , hospital_pwd = #{hospital_pwd}

	 , mdfy_by = 'admin'
WHERE 1=1
  AND hospital_id = #{hospital_id}
