mapper.select:
SELECT t1.user_id , t1.user_pwd , t1.user_type , t1.mobile_tel_no FROM (SELECT user_id , user_pwd , user_type , mobile_tel_no FROM t_user_info WHERE 1=1 AND user_type = 'ADMIN' UNION ALL SELECT hospital_id AS user_id , hospital_pwd AS user_pwd , 'HOSPITAL' AS user_type , mgr_tel_no AS mobile_tel_no FROM t_hospital_info) AS t1 WHERE 1=1 AND t1.mobile_tel_no = #{mobile_tel_no}
AND t1.user_id = #{user_id}
ORDER BY user_type , user_id

mapper.update:
UPDATE t_hospital_info
   SET hospital_pwd = #{temp_pwd}
 WHERE mgr_tel_no = #{mobile_tel_no}
   AND hospital_id = #{user_id}

UPDATE t_hospital_info
   SET last_login = now()
     , refresh_token = #{refresh_token}
 WHERE hospital_id = #{user_id}
